function writeCcode(struct, svar, fid, ip, jp)

splineflag = struct.splineflag;

nx = struct.nx;
ny = struct.ny;
nr = struct.nr;
if(strcmp(svar,'xdot'))
    fprintf(fid,['  memset(xdot_tmp,0,sizeof(double)*' num2str(nx) ');\n']);
    cvar =  'xdot_tmp';
