function gccode(csym,cvar,fid,splineflag)

if(length(csym) == 1)
    cstr = [cvar ' = ' char(csym) ';'];
    cstr = regexprep(cstr,'\(([a-zA-Z0-9\[\]\*\s\+\-\/\^]*)\)\^([0-9]*)','pow($1,$2)'); % braced scalar exponentiation
    cstr = regexprep(cstr,'([a-zA-Z]*\[[0-9]*\])\^([0-9]*)','pow($1,$2)'); % scalar scalar exponentiation
    cstr = regexprep(cstr,'([a-zA-Z]*\[[0-9]*\])\^(\([a-zA-Z0-9\[\]])\))','pow($1,$2)'); % scalar braced exponentiation
    cstr = regexprep(cstr,'(\([a-zA-Z0-9\[\]])\))\^(\([a-zA-Z0-9\[\]])\))','pow($1,$2)'); % braced braced exponentiation
    cstr = regexprep(cstr,'([a-zA-Z0-9])\^([a-zA-Z0-9])','pow($1,$2)'); % t^2
    if(strfind(cstr,'^'))
        % looks like we did not succeed, so do this the proper way
        error('cvodewrap cannot parse system') % this wont actually work yet as generate::C reduces indexing by 1
        temp = sprintf(mupadmex('generate::C', [cvar ' = ' char(csym)], 0));
        temp(temp == '"') = [];
        cstr = deblank(temp);
    end
    cstr = deblank(cstr);
    
    if(strcmp(cvar,'qBdot_tmp'))
        cstr = regexprep(cstr,'qBdot_tmp\[([0-9]*)\]','qBdot_tmp\[$1+ip*ny]');
